    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       String usuario = jTextField1.getText();
       String passwd = jPasswordField1.getText();
       
       if(usuario.isEmpty() || passwd.isEmpty()){
           JOptionPane.showMessageDialog(null, "Algun campo esta vacio");
       }else{
           if(usuario.equals("admin1") && passwd.equals("1234")){
               JOptionPane.showMessageDialog(null, "Bienvenido");
               panel_control_admin pc = new panel_control_admin();
               pc.setVisible(true);
               pc.setLocationRelativeTo(null);
               this.dispose();
               
           }else{
                if(usuario.equals("vendedor1") && passwd.equals("1234")){
               JOptionPane.showMessageDialog(null, "Bienvenido");
               panel_control_vendedor pc = new panel_control_vendedor();
               pc.setVisible(true);
               pc.setLocationRelativeTo(null);
               this.dispose();
                }else{
                    if(usuario.equals("tele1") && passwd.equals("1234")){
               JOptionPane.showMessageDialog(null, "Bienvenido");
               panel_control_tele pc = new panel_control_tele();
               pc.setVisible(true);
               pc.setLocationRelativeTo(null);
               this.dispose();
                    }else{
               JOptionPane.showMessageDialog(null, "Su usuario o contrase침a son incorrectos");
                }
              }     
           }
       }
    }//GEN-LAST:event_jButton1ActionPerformed

